import React from "react";

const LoginPage = () => {
  return (
    <div style={styles.container}>
      {/* Left Section (Login) - Updated Design */}
      <div style={styles.leftSection}>
        <h2 style={styles.title}>Sign in to Your App</h2>

        {/* Social Media Icons */}
        <div style={styles.socialIcons}>
          <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" style={styles.iconCircle}>
            <i className="fab fa-facebook-f"></i>
          </a>
          <a href="https://www.google.com" target="_blank" rel="noopener noreferrer" style={styles.iconCircle}>
            <i className="fab fa-google"></i>
          </a>
          <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer" style={styles.iconCircle}>
            <i className="fab fa-linkedin-in"></i>
          </a>
        </div>

        <p style={styles.orText}>or use your email account:</p>

        {/* Input Fields */}
        <div style={styles.inputContainer}>
          <div style={styles.inputWrapper}>
            <i className="fas fa-envelope" style={styles.inputIcon}></i>
            <input type="email" placeholder="Email" style={styles.input} />
          </div>
          <div style={styles.inputWrapper}>
            <i className="fas fa-lock" style={styles.inputIcon}></i>
            <input type="password" placeholder="Password" style={styles.input} />
          </div>
        </div>

        {/* Forgot Password */}
        <p style={styles.forgotPassword}>Forgot your password?</p>

        {/* Sign In Button */}
        <button style={styles.signInButton}>SIGN IN</button>
      </div>

      {/* Vertical Divider */}
      <div style={styles.divider}></div>

      {/* Right Section (Sign Up) - Remains the Same */}
      <div style={styles.rightSection}>
        <h2 style={styles.signupTitle}>New Here?</h2>
        <p style={styles.signupText}>
          Sign up and discover a great amount of educational events!
        </p>
        <button style={styles.signupButton}>SIGN UP</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    width: "100vw",
    background: "#FFFFFF", // White background
    fontFamily: "'Poppins', sans-serif",
  },
  leftSection: {
    flex: 2, // Takes more space
    padding: "40px",
    textAlign: "center",
  },
  rightSection: {
    flex: 1, // Takes less space
    padding: "40px",
    textAlign: "center",
    background: "#53917E", // Full background teal
    color: "white",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    fontSize: "26px",
    fontWeight: "bold",
    color: "#53917E", // Match the modern look
  },
  socialIcons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    marginBottom: "15px",
  },
  iconCircle: {
    width: "45px",
    height: "45px",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "50%",
    backgroundColor: "#f1f1f1",
    color: "#000",
    fontSize: "20px",
    cursor: "pointer",
    textDecoration: "none",
    transition: "background 0.3s ease-in-out",
  },
  orText: {
    fontSize: "14px",
    color: "#666",
    marginBottom: "10px",
  },
  inputContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "10px",
  },
  inputWrapper: {
    display: "flex",
    alignItems: "center",
    background: "#f9f9f9",
    borderRadius: "8px",
    padding: "10px",
    width: "300px",
    border: "1px solid #ddd",
  },
  inputIcon: {
    color: "#999",
    marginRight: "10px",
  },
  input: {
    width: "100%",
    border: "none",
    background: "transparent",
    outline: "none",
    fontSize: "14px",
  },
  forgotPassword: {
    fontSize: "14px",
    color: "#6D1A36",
    cursor: "pointer",
    marginTop: "10px",
    textDecoration: "underline",
  },
  signInButton: {
    width: "300px",
    padding: "12px",
    marginTop: "15px",
    background: "#53917E",
    color: "white",
    border: "none",
    borderRadius: "30px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background 0.3s ease-in-out",
  },
  signupTitle: {
    fontSize: "24px",
    fontWeight: "bold",
  },
  signupText: {
    fontSize: "14px",
    marginBottom: "15px",
    textAlign: "center",
  },
  signupButton: {
    background: "#6D1A36",
    color: "white",
    padding: "12px 20px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background 0.3s ease-in-out",
  },
  divider: {
    width: "2px",
    background: "#B1B695", // Soft greenish-gray
    height: "100vh", // Ensure it touches the top and bottom
  },
};

// Adding hover effects
document.addEventListener("DOMContentLoaded", () => {
  const icons = document.querySelectorAll(".fab");
  icons.forEach((icon) => {
    icon.addEventListener("mouseover", () => {
      icon.style.background = "#e0e0e0"; // Lighter background hover effect
    });
    icon.addEventListener("mouseout", () => {
      icon.style.background = "#f1f1f1"; // Default color
    });
  });

  const signInBtn = document.querySelector("button[style*=signInButton]");
  const signUpBtn = document.querySelector("button[style*=signupButton]");

  signInBtn.addEventListener("mouseover", () => {
    signInBtn.style.background = "#6D1A36";
  });
  signInBtn.addEventListener("mouseout", () => {
    signInBtn.style.background = "#53917E";
  });

  signUpBtn.addEventListener("mouseover", () => {
    signUpBtn.style.background = "#FCD0A1"; // Soft peach hover
  });
  signUpBtn.addEventListener("mouseout", () => {
    signUpBtn.style.background = "#6D1A36";
  });
});

export default LoginPage;
