import React from "react";

const LoginPage = () => {
  return (
    <div style={styles.container}>
      {/* Left Section (Login) - Takes More Space */}
      <div style={styles.leftSection}>
        <h2 style={styles.title}>Welcome Back!</h2>
        <p style={styles.subText}>
          To keep connected with us please log in with your personal info
        </p>

        <div style={styles.socialIcons}>
          <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" style={styles.icon}>
            <i className="fab fa-facebook-f"></i>
          </a>
          <a href="https://www.google.com" target="_blank" rel="noopener noreferrer" style={styles.icon}>
            <i className="fab fa-google"></i>
          </a>
          <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer" style={styles.icon}>
            <i className="fab fa-linkedin-in"></i>
          </a>
        </div>

        <p style={styles.orText}>or use your email</p>

        <input type="email" placeholder="Email" style={styles.input} />
        <div style={styles.passwordContainer}>
          <input type="password" placeholder="Password" style={styles.passwordInput} />
          <span style={styles.eyeIcon}>üëÅÔ∏è</span>
        </div>
        <button style={styles.signInButton}>SIGN IN</button>
      </div>

      {/* Vertical Divider */}
      <div style={styles.divider}></div>

      {/* Right Section (Sign Up) - Takes Less Space */}
      <div style={styles.rightSection}>
        <h2 style={styles.signupTitle}>New Here?</h2>
        <p style={styles.signupText}>
          Sign up and discover a great amount of educational events!
        </p>
        <button style={styles.signupButton}>SIGN UP</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    width: "100vw",
    background: "#FFFFFF", // White background on the left side
    fontFamily: "'Poppins', sans-serif",
  },
  leftSection: {
    flex: 2, // Takes more space
    padding: "40px",
    textAlign: "center",
  },
  rightSection: {
    flex: 1, // Takes less space
    padding: "40px",
    textAlign: "center",
    background: "#53917E", // Full background teal
    color: "white",
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    fontSize: "24px",
    fontWeight: "bold",
    color: "#000", // Black text
  },
  subText: {
    fontSize: "14px",
    color: "#333",
    marginBottom: "15px",
  },
  socialIcons: {
    display: "flex",
    justifyContent: "center",
    gap: "15px",
    marginBottom: "10px",
  },
  icon: {
    fontSize: "24px",
    color: "#53917E", // Teal color for consistency
    textDecoration: "none",
    transition: "color 0.3s ease-in-out",
  },
  iconHover: {
    color: "#6D1A36", // Change to maroon on hover
  },
  orText: {
    margin: "10px 0",
    color: "#555",
  },
  input: {
    width: "100%",
    padding: "10px",
    margin: "8px 0",
    borderRadius: "5px",
    border: "1px solid #AAA",
    fontSize: "14px",
  },
  passwordContainer: {
    position: "relative",
  },
  passwordInput: {
    width: "100%",
    padding: "10px",
    borderRadius: "5px",
    border: "1px solid #AAA",
    fontSize: "14px",
  },
  eyeIcon: {
    position: "absolute",
    right: "10px",
    top: "50%",
    transform: "translateY(-50%)",
    cursor: "pointer",
  },
  signInButton: {
    width: "100%",
    padding: "12px",
    marginTop: "10px",
    background: "#53917E",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: "bold",
    transition: "background 0.3s ease-in-out",
  },
  signupTitle: {
    fontSize: "24px",
    fontWeight: "bold",
  },
  signupText: {
    fontSize: "14px",
    marginBottom: "15px",
    textAlign: "center",
  },
  signupButton: {
    background: "#6D1A36",
    color: "white",
    padding: "12px 20px",
    borderRadius: "5px",
    border: "none",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background 0.3s ease-in-out",
  },
  divider: {
    width: "2px",
    background: "#B1B695", // Soft greenish-gray
    height: "100vh", // Ensure it touches the top and bottom
  },
};

// Adding hover effects
document.addEventListener("DOMContentLoaded", () => {
  const icons = document.querySelectorAll(".fab");
  icons.forEach((icon) => {
    icon.addEventListener("mouseover", () => {
      icon.style.color = "#6D1A36"; // Maroon hover color
    });
    icon.addEventListener("mouseout", () => {
      icon.style.color = "#53917E"; // Back to teal
    });
  });

  const signInBtn = document.querySelector("button[style*=signInButton]");
  const signUpBtn = document.querySelector("button[style*=signupButton]");

  signInBtn.addEventListener("mouseover", () => {
    signInBtn.style.background = "#6D1A36";
  });
  signInBtn.addEventListener("mouseout", () => {
    signInBtn.style.background = "#53917E";
  });

  signUpBtn.addEventListener("mouseover", () => {
    signUpBtn.style.background = "#FCD0A1"; // Soft peach hover
  });
  signUpBtn.addEventListener("mouseout", () => {
    signUpBtn.style.background = "#6D1A36";
  });
});

export default LoginPage;
