// src/pages/LoginPage.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [isSignInHovered, setIsSignInHovered] = useState(false);

  // 1) Initialize the navigate function
  const navigate = useNavigate();

  // We'll keep the styles inline, but you can move them outside or into a CSS file
  const styles = {
    container: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      width: "100vw",
      background: "#FFFFFF",
      fontFamily: "'Poppins', sans-serif",
    },
    leftSection: {
      flex: 2,
      padding: "40px",
      textAlign: "center",
    },
    rightSection: {
      flex: 1,
      padding: "40px",
      textAlign: "center",
      background: "linear-gradient(135deg, #B1B695, #53917E)",
      color: "white",
      height: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
    },
    title: {
      fontSize: "26px",
      fontWeight: "bold",
      color: "#53917E",
    },
    // ... other style objects ...
    signInButton: {
      width: "300px",
      padding: "12px",
      marginTop: "15px",
      background: "linear-gradient(135deg, #B1B695, #53917E)",
      color: "white",
      border: "none",
      borderRadius: "30px",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold",
      transition: "transform 0.3s ease-in-out, background 0.3s ease-in-out",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    },
    signUpButton: {
      width: "300px",
      padding: "12px",
      marginTop: "15px",
      background: "#fff",
      color: "#53917E",
      border: "none",
      borderRadius: "30px",
      cursor: "pointer",
      fontSize: "16px",
      fontWeight: "bold",
      transition: "transform 0.3s ease-in-out, background 0.3s ease-in-out",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    },
    divider: {
      width: "2px",
      background: "#B1B695",
      height: "100vh",
    },
    signupTitle: {
      fontSize: "28px",
      marginBottom: "10px",
    },
    signupText: {
      fontSize: "18px",
      margin: "0 30px",
    },
  };

  return (
    <div style={styles.container}>
      {/* Left Section (Login) */}
      <div style={styles.leftSection}>
        <h2 style={styles.title}>Login in to Your Account</h2>
        {/* ... your existing input code, social icons, etc. ... */}

        <button
          style={{
            ...styles.signInButton,
            transform: isSignInHovered ? "scale(1.05)" : "scale(1)",
          }}
          onMouseEnter={() => setIsSignInHovered(true)}
          onMouseLeave={() => setIsSignInHovered(false)}
        >
          SIGN IN
        </button>
      </div>

      <div style={styles.divider}></div>

      {/* Right Section (Sign Up) */}
      <div style={styles.rightSection}>
        <h1 style={styles.signupTitle}>New Here?</h1>
        <h2 style={styles.signupText}>
          Sign up and discover a great amount of educational events!
        </h2>
        <button
          style={{
            ...styles.signUpButton,
            background: isHovered ? "#f3f3f3" : "#fff",
            transform: isHovered ? "scale(1.05)" : "scale(1)",
          }}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
          // 2) On click, navigate to "/signup"
          onClick={() => navigate("/signup")}
        >
          SIGN UP
        </button>
      </div>
    </div>
  );
};

export default LoginPage;
